// import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Trail = props => {
    return(
  // <Layout>
  <div>
    <h1>{props.trail.name}</h1>
    <p>{props.trail.summary}</p>
    </div>
    // {/* {props.show.image ? <img src={props.show.image.medium} /> : null} */}
  // </Layout>
);
    }

Trail.getInitialProps = async function(context) {
    const { id } = context.query;
    console.log(context);
    const res = await fetch(`https://cors-anywhere.herokuapp.com/https://www.hikingproject.com/data/get-trails-by-id?ids=${id}&key=200465360-942e3fb792b81fa531e25b7484cbc0f9`);
    const trail = await res.json();

    return { trail: trail.trails[0] };
  };

export default Trail;
