import Link from 'next/link';

const IntroPage = () => {
    return(
    <div className="content-container">
        <div className="title-wrapper">
            <h1 className="title">Yeah, quarantine sucks. <span>Big time.</span> But that doesn't mean that there isn't fun to be had.</h1>
            <Link href="/choice">
                <a className="global-button">Right?</a>
            </Link>
        </div>
        <style jsx>{`
            .content-container {
                // margin: 0 1rem;
                height: 100vh;
                background: linear-gradient(#e66465, #9198e5);
                position: relative;
            }
            .title-wrapper {
                display: grid;
                margin: 0 auto;
                width: 80%;
                justify-items: center;
                text-align: center;
                position: absolute;
                top: 10%;
                left: 50%;
                transform: translate(-50%, 50%);
            }
            .title {
                color: white;
                opacity: 1;
                font-size: 4rem;
            }

            .title span {
                font-style: italic;
                font-weight: 500;
            }
            .global-button {
                text-decoration: none;
                padding: 1rem 1.75rem;
                background-color: blue;
                color: white;
                border-radius 2rem;
                font-weight: 700;
                // border: 2px solid darkblue;

            }
            .global-button:hover {
                background-color: darkBlue;
            }
    `}</style>
    </div>
    )
}

export default IntroPage
