Book.getInitialProps = async function(context) {
    const { title } = context.query;
    console.log(context);
    const res = await fetch(`https://api.nytimes.com/svc/books/v3//reviews.json?title=${title}&api-key=FKUX6ohX3FTf4TmzHnlcnZIZMApf0iCc`);
    const review = await res.json();

    return { review: review.results[0] };
    };

export default Book;
